        gerarBarraProgresso(){

            let date = new Date();

            let horaAtual = date.getHours();
            let horaTotal = 24;
            let totalPorcentagem = 100;

            let porcentagem = ((horaAtual / horaTotal) * totalPorcentagem).toFixed(2);

            return `
                <div class="progress-countdown"><span class="progress-countdown-line" style="width:${porcentagem}%"></span></div>
            `;

        },

        formatarPrecoParaBRL(price){

            let $price = price.toLowerCase().trim();

            return parseFloat($price).toLocaleString('pt-br', {minimumFractionDigits: 2});
            // formatPrice('5200.54') = 5.200,54
        },

        formatarPrecoParaPadraoJS(price){
            if(price == ''){
                return price;
            }

            let $price = price.toLowerCase().trim();
            if($price.includes('r$ ')){
                return $price.replace('r$', '').trim().replace(/\./g, '').replace(',', '.');
            }
            
            return $price.replace(/\./g, '').replace(',', '.');
            // convertPriceBRLToDefault('5.200,54' || 'R$ 5.200,54') = 5200.54
        },

        setarCookie(name, value, hours){
            if (hours){
                var date = new Date();
                date.setTime(date.getTime()+(hours*60*60*1000));
                var expires = "; expires="+date.toGMTString();
            }
            else{
                var expires = "";
            }
            document.cookie = name+"="+value+expires+"; path=/";
        },

