                alertaFreteGratisPorRegiao(){

                    var minValues = {
                        'Sul': 299.0,
                        'Sudeste': 299.0,
                        'Norte': 299.0,
                        'Nordeste': 299.0,
                        'Centro Oeste': 299.0
                    };
                   
                    var cepRanges = {
                        'AC': [
                            [69900000, 69999999]
                        ],
                        'AL': [
                            [57000000, 57999999]
                        ],
                        'AM': [
                            [69000000, 69299999],
                            [69400000, 69899999]
                        ],
                        'AP': [
                            [68900000, 68999999]
                        ],
                        'BA': [
                            [40000000, 48999999]
                        ],
                        'CE': [
                            [60000000, 63999999]
                        ],
                        'DF': [
                            [70000000, 72799999],
                            [73000000, 73699999]
                        ],
                        'ES': [
                            [29000000, 29999999]
                        ],
                        'GO': [
                            [72800000, 72999999],
                            [73700000, 76799999]
                        ],
                        'MA': [
                            [65000000, 65999999]
                        ],
                        'MG': [
                            [30000000, 39999999]
                        ],
                        'MS': [
                            [79000000, 79999999]
                        ],
                        'MT': [
                            [78000000, 78899999]
                        ],
                        'PA': [
                            [66000000, 68899999]
                        ],
                        'PB': [
                            [58000000, 58999999]
                        ],
                        'PE': [
                            [50000000, 56999999]
                        ],
                        'PI': [
                            [64000000, 64999999]
                        ],
                        'PR': [
                            [80000000, 87999999]
                        ],
                        'RJ': [
                            [20000000, 28999999]
                        ],
                        'RN': [
                            [59000000, 59999999]
                        ],
                        'RO': [
                            [76800000, 76999999]
                        ],
                        'RR': [
                            [69300000, 69399999]
                        ],
                        'RS': [
                            [90000000, 99999999]
                        ],
                        'SC': [
                            [88000000, 89999999]
                        ],
                        'SE': [
                            [49000000, 49999999]
                        ],
                        'SP': [
                            [01000000, 19999999]
                        ],
                        'TO': [
                            [77000000, 77999999]
                        ]
                    };

                    var regions = {
                        'Norte': ['AC', 'AM', 'AP', 'PA', 'RO', 'RR', 'TO'],
                        'Nordeste': ['AL', 'BA', 'CE', 'MA', 'PB', 'PE', 'PI', 'RN', 'SE'],
                        'Centro Oeste': ['DF', 'GO', 'MS', 'MT'],
                        'Sudeste': ['ES', 'MG', 'RJ', 'SP'],
                        'Sul': ['RS', 'SC', 'PR']
                    };

                    function getMinValue(a) {
                        return minValues[a]
                    }

                    function getRegion(c) {
                        var d = null;
                        $.each(regions, function(a, b) {
                            for (var i = 0; i < b.length; i++) {
                                if (c == b[i]) {
                                    d = a;
                                    return false
                                }
                            }
                        });
                        return d
                    }

                    function getUF(e) {
                        var f = null;
                        $.each(cepRanges, function(a, b) {
                            for (var i = 0; i < b.length; i++) {
                                var c = b[i][0];
                                var d = b[i][1];
                                if (e >= c && e <= d) {
                                    f = a;
                                    return false
                                }
                            }
                        });
                        return f
                    }

                    function cleanCep(a) {
                        return a.replace('-', '')
                    }

                    function getTotal() {
                        var a = $('.subtotal .titulo').attr('data-subtotal-valor');
                        var b = $('.cupom-sucesso').length;
                        if (b == 0) {
                            return a
                        } else if (b > 0) {
                            var c = $('#cupom_desconto').html();
                            if (!c) {
                                return a
                            }
                            c = c.replace(/^\s+|\s+$/g, '');
                            var d = /%/i.test(c);
                            var e = /R/i.test(c);
                            var f = c.split(' ');
                            if (d) {
                                var g = a - (a * f[0] / 100)
                            } else if (e) {
                                var g = a - parseInt(f[1])
                            }
                            return g
                        }
                    }

                    function calculate() {

                        
                        var a = 'Quer Ganhar Frete Grtis? Informe o seu cep e saiba quanto falta para ganhar.';
                        var b = $('#calcularFrete').val() || 0;
                        var c = $('.cupom-sucesso').length;
                        var d = 'frete-gratis falta-cep';
                        var percent = 0;
                        if (b.length == 9) {
                            b = cleanCep(b);
                            var e = getUF(b);
                            var f = getRegion(e);
                            var g = getMinValue(f);
                            var percent = 100 - ((g - getTotal()) / g * 100);
                                
                            if (!g) {
                                a = 'Ops! No h opo de frete grtis para sua regio.'
                                percent = 0;
                            } else {
                                var h = getTotal();
                                if (h < g) {
                                    var i = (g - h).toFixed(2).toString().replace('.', ',');
                                    d = 'frete-gratis falta-pouco';
                                    a = 'FALTAM R$ ' + i + ' em compras pra voc GANHAR FRETE GRTIS!'
                                } else if (h >= g) {
                                    percent = 100;
                                    d = 'frete-gratis ganhou-frete';
                                    a = 'Parabns! Voc Ganhou Frete Grtis!'
                                }
                            }
                        }
                            
                        var j = `<div class="frete-gratis-por-regiao section-progress-bar">
                                    <div class="section-progress-bar--wrap progress-bar">
                                        <span>${a}</span>
                                        <div class="progress-bar--conteudo ${d}" style="width: ${percent}% !important;"></div>
                                    </di>
                                </div>`;
                            
                        //var j = '<div class="alert ' + d + '" role="alert">' + a + '</div>';
                        
                        $('.section-progress-bar').fadeOut();
                        $('.section-progress-bar').remove();
                        $('.finalizar-compra > .caixa-sombreada').before(j);
                        $('.section-progress-bar').fadeIn();
                    }

                    $(document).ready(function() {
                        if ($("#calcularFrete").is(":hidden")) {
                            $("#calcularFrete").parent().closest("tr").removeClass("hidden-phone")
                        }
                        $("#calcularFrete").on('input', function(e) {
                            calculate()
                        });
                        calculate()
                    });

                },

