                acoesEmModalPlay(){ //AÇÕES EM TODOS MODAIS DE PLAY, OUÇA DICA, VEJA O VÍDEO, VÍDEO DO PRODUTO.
                    $(window).on('load', function(){

                        function callPlayer(frame_id, func, args) {

                            if (window.jQuery && frame_id instanceof jQuery) frame_id = frame_id.get(0).id;
                            var iframe = document.getElementById(frame_id);
                            if (iframe && iframe.tagName.toUpperCase() != 'IFRAME') {
                                iframe = iframe.getElementsByTagName('iframe')[0];
                            }
                            if (iframe) {
                                // Frame exists, 
                                iframe.contentWindow.postMessage(JSON.stringify({
                                    "event": "command",
                                    "func": func,
                                    "args": args || [],
                                    "id": frame_id
                                }), "*");
                            }
                        }

                        $(document).on('hidden.bs.modal', '.modal_play_iluminim', function(){

                            let iframe_id = $(this).find('iframe').attr('id');
                            callPlayer(iframe_id,'pauseVideo');

                        });

                    });
                    
                },
                
